import { Component } from "react";
import Course from "./components/Course";
import { courses } from "./data";
import Navbar from "./components/Navbar";

class List extends Component {
  constructor(props) {
    super(props);
    this.state = {
      bagItems: [],
      courseCount: 0
    };
  }

  isCourseinBag = (id) => {
    return !!this.state.bagItems.find((c) => c.id === id);
  };

  handleAddToBag = (id) => {
    const course = courses.find((c) => c.id === id);
    this.setState({
      bagItems: [course, ...this.state.bagItems],
      courseCount: courseCount + 1
    });
  };

  handleRemoveFromBag = (id) => {
    const filteredCourses = this.state.bagItems.filter((c) => c.id !== id);
    this.setState({ bagItems: filteredCourses });
  };

  render() {
    const{courses}=this.props

    const {courseCount}=this.state
    return (
      <div className="list">
         <Navbar/>
        {courses.map((v) => (
         
          <Course
            key={v.id}
            video={v}
            onAdd={this.handleAddToBag}
            onRemove={this.handleRemoveFromBag}
            isInBag={this.isCourseinBag(v.id)}
          />
        ))}
      </div>
    );
  }
}

export default List;
